package com.waterfountainmachine.app

import android.animation.ObjectAnimator
import android.content.Intent
import android.os.Bundle
import android.view.KeyEvent
import android.view.View
import android.view.WindowManager
import android.view.animation.AccelerateDecelerateInterpolator
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.core.view.WindowInsetsControllerCompat
import com.waterfountainmachine.app.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupKioskMode()
        setupFullScreen()
        setupClickListener()
        setupQuestionMarkAnimation()
        setupModalFunctionality()
    }

    private fun setupKioskMode() {
        // Keep screen on
        window.addFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON)

        // Prevent task switching
        window.addFlags(WindowManager.LayoutParams.FLAG_DISMISS_KEYGUARD)
        window.addFlags(WindowManager.LayoutParams.FLAG_SHOW_WHEN_LOCKED)
        window.addFlags(WindowManager.LayoutParams.FLAG_TURN_SCREEN_ON)

        // Make this a launcher activity and clear task
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK)
    }

    private fun setupFullScreen() {
        WindowCompat.setDecorFitsSystemWindows(window, false)
        val controller = WindowInsetsControllerCompat(window, binding.root)
        controller.hide(WindowInsetsCompat.Type.systemBars())
        controller.systemBarsBehavior = WindowInsetsControllerCompat.BEHAVIOR_SHOW_TRANSIENT_BARS_BY_SWIPE

        // Hide system UI completely
        binding.root.systemUiVisibility = (View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                or View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                or View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_FULLSCREEN
                or View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY)
    }

    private fun setupClickListener() {
        binding.root.setOnClickListener {
            // Only navigate if modal is not visible
            if (binding.modalOverlay.visibility == View.GONE) {
                val intent = Intent(this, VendingActivity::class.java)
                startActivity(intent)
                overridePendingTransition(R.anim.slide_in_right, R.anim.slide_out_left)
            }
        }
    }

    private fun setupQuestionMarkAnimation() {
        // Create a subtle shaking animation for the question mark
        val shakeAnimation = ObjectAnimator.ofFloat(binding.questionMarkIcon, "rotation", -5f, 5f, -5f, 5f, 0f)
        shakeAnimation.duration = 2000
        shakeAnimation.repeatCount = ObjectAnimator.INFINITE
        shakeAnimation.repeatMode = ObjectAnimator.RESTART
        shakeAnimation.interpolator = AccelerateDecelerateInterpolator()
        shakeAnimation.start()
    }

    private fun setupModalFunctionality() {
        // Question mark button click
        binding.questionMarkButton.setOnClickListener {
            showModal()
        }

        // Close modal button click
        binding.closeModalButton.setOnClickListener {
            hideModal()
        }

        // Click outside modal to close
        binding.modalOverlay.setOnClickListener { view ->
            if (view == binding.modalOverlay) {
                hideModal()
            }
        }

        // Prevent modal content clicks from closing modal
        binding.modalContent.setOnClickListener {
            // Do nothing - prevent click from bubbling up
        }
    }

    private fun showModal() {
        binding.modalOverlay.visibility = View.VISIBLE

        // Animate modal appearance
        binding.modalContent.alpha = 0f
        binding.modalContent.scaleX = 0.8f
        binding.modalContent.scaleY = 0.8f

        binding.modalContent.animate()
            .alpha(1f)
            .scaleX(1f)
            .scaleY(1f)
            .setDuration(300)
            .setInterpolator(AccelerateDecelerateInterpolator())
            .start()
    }

    private fun hideModal() {
        binding.modalContent.animate()
            .alpha(0f)
            .scaleX(0.8f)
            .scaleY(0.8f)
            .setDuration(250)
            .setInterpolator(AccelerateDecelerateInterpolator())
            .withEndAction {
                binding.modalOverlay.visibility = View.GONE
            }
            .start()
    }

    // Prevent back button from exiting app
    override fun onBackPressed() {
        // Do nothing - disable back button
    }

    // Prevent hardware keys from exiting
    override fun onKeyDown(keyCode: Int, event: KeyEvent?): Boolean {
        return when (keyCode) {
            KeyEvent.KEYCODE_HOME,
            KeyEvent.KEYCODE_RECENT_APPS,
            KeyEvent.KEYCODE_BACK -> true // Block these keys
            else -> super.onKeyDown(keyCode, event)
        }
    }

    override fun onWindowFocusChanged(hasFocus: Boolean) {
        super.onWindowFocusChanged(hasFocus)
        if (hasFocus) {
            setupFullScreen()
        }
    }
}

package com.waterfountainmachine.app

import android.animation.AnimatorSet
import android.animation.ObjectAnimator
import android.content.Intent
import android.os.Bundle
import android.view.KeyEvent
import android.view.View
import android.view.WindowManager
import android.view.animation.AccelerateDecelerateInterpolator
import android.view.animation.DecelerateInterpolator
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.core.view.WindowInsetsControllerCompat
import com.waterfountainmachine.app.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding
    private var questionMarkAnimator: AnimatorSet? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupKioskMode()
        setupFullScreen()
        setupClickListener()
        setupQuestionMarkAnimation()
        setupModalFunctionality()
        setupPressAnimation()
    }

    private fun setupKioskMode() {
        // Keep screen on
        window.addFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON)

        // Prevent task switching
        window.addFlags(WindowManager.LayoutParams.FLAG_DISMISS_KEYGUARD)
        window.addFlags(WindowManager.LayoutParams.FLAG_SHOW_WHEN_LOCKED)
        window.addFlags(WindowManager.LayoutParams.FLAG_TURN_SCREEN_ON)

        // Make this a launcher activity and clear task
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK)
    }

    private fun setupFullScreen() {
        WindowCompat.setDecorFitsSystemWindows(window, false)
        val controller = WindowInsetsControllerCompat(window, binding.root)
        controller.hide(WindowInsetsCompat.Type.systemBars())
        controller.systemBarsBehavior = WindowInsetsControllerCompat.BEHAVIOR_SHOW_TRANSIENT_BARS_BY_SWIPE

        // Hide system UI completely
        binding.root.systemUiVisibility = (View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                or View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                or View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_FULLSCREEN
                or View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY)
    }

    private fun setupClickListener() {
        binding.root.setOnClickListener {
            // Only navigate if modal is not visible
            if (binding.modalOverlay.visibility == View.GONE) {
                performPressAnimation {
                    val intent = Intent(this, VendingActivity::class.java)
                    startActivity(intent)
                    // Use Material Design's shared axis Z transition
                    overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out)
                }
            }
        }
    }

    private fun setupQuestionMarkAnimation() {
        // Create a more frequent and engaging shake animation
        val shakeX = ObjectAnimator.ofFloat(binding.questionMarkButton, "translationX", 0f, -12f, 12f, -8f, 8f, -4f, 4f, 0f)
        shakeX.duration = 1000 // Shorter duration

        val rotate = ObjectAnimator.ofFloat(binding.questionMarkIcon, "rotation", 0f, 20f, -20f, 15f, -15f, 8f, -8f, 0f)
        rotate.duration = 1000

        val scale = ObjectAnimator.ofFloat(binding.questionMarkButton, "scaleX", 1f, 1.15f, 1f)
        scale.duration = 1000

        val scaleY = ObjectAnimator.ofFloat(binding.questionMarkButton, "scaleY", 1f, 1.15f, 1f)
        scaleY.duration = 1000

        questionMarkAnimator = AnimatorSet().apply {
            playTogether(shakeX, rotate, scale, scaleY)
            interpolator = AccelerateDecelerateInterpolator()
            startDelay = 1000 // Start sooner
        }

        // Create repeating animation with much shorter intervals
        questionMarkAnimator?.addListener(object : android.animation.AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: android.animation.Animator) {
                // Much more frequent shaking
                binding.root.postDelayed({
                    if (questionMarkAnimator != null) {
                        questionMarkAnimator?.start()
                    }
                }, 1500) // Only 1.5 second pause between animations
            }
        })

        questionMarkAnimator?.start()
    }

    private fun setupPressAnimation() {
        // Add breathing animation to instruction text
        setupBreathingAnimation()

        // Add touch feedback to the entire screen
        binding.root.setOnTouchListener { _, event ->
            when (event.action) {
                android.view.MotionEvent.ACTION_DOWN -> {
                    // Create haptic-like ripple effect
                    createRippleEffect(event.x, event.y)
                    // Animate ONLY the content, not the background
                    binding.mainContent.animate()
                        .scaleX(0.98f)
                        .scaleY(0.98f)
                        .setDuration(100)
                        .setInterpolator(DecelerateInterpolator())
                        .start()
                }
                android.view.MotionEvent.ACTION_UP, android.view.MotionEvent.ACTION_CANCEL -> {
                    // Scale back to normal
                    binding.mainContent.animate()
                        .scaleX(1f)
                        .scaleY(1f)
                        .setDuration(150)
                        .setInterpolator(DecelerateInterpolator())
                        .start()
                }
            }
            false // Let the click listener handle the actual click
        }
    }

    private fun setupBreathingAnimation() {
        // Create a more engaging "pulse glow" animation instead of just breathing
        val glowIn = ObjectAnimator.ofFloat(binding.instructionText, "alpha", 0.7f, 1f)
        glowIn.duration = 1200

        val glowOut = ObjectAnimator.ofFloat(binding.instructionText, "alpha", 1f, 0.7f)
        glowOut.duration = 1200

        // Add a subtle color shift effect by changing text shadow/elevation
        val elevateIn = ObjectAnimator.ofFloat(binding.instructionText, "translationZ", 0f, 8f)
        elevateIn.duration = 1200

        val elevateOut = ObjectAnimator.ofFloat(binding.instructionText, "translationZ", 8f, 0f)
        elevateOut.duration = 1200

        // Add a gentle wave-like scale effect
        val scaleInX = ObjectAnimator.ofFloat(binding.instructionText, "scaleX", 1f, 1.03f)
        scaleInX.duration = 1200

        val scaleInY = ObjectAnimator.ofFloat(binding.instructionText, "scaleY", 1f, 1.03f)
        scaleInY.duration = 1200

        val scaleOutX = ObjectAnimator.ofFloat(binding.instructionText, "scaleX", 1.03f, 1f)
        scaleOutX.duration = 1200

        val scaleOutY = ObjectAnimator.ofFloat(binding.instructionText, "scaleY", 1.03f, 1f)
        scaleOutY.duration = 1200

        val pulseSet = AnimatorSet().apply {
            play(glowIn).with(elevateIn).with(scaleInX).with(scaleInY)
            play(glowOut).with(elevateOut).with(scaleOutX).with(scaleOutY).after(glowIn)
            interpolator = AccelerateDecelerateInterpolator()
        }

        // Make it repeat continuously with shorter intervals
        pulseSet.addListener(object : android.animation.AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: android.animation.Animator) {
                // Shorter pause between pulses for more engagement
                binding.root.postDelayed({
                    pulseSet.start()
                }, 500) // Only 0.5 second pause
            }
        })

        pulseSet.start()
    }

    private fun createRippleEffect(x: Float, y: Float) {
        // Only use the circular ripple effect - no background manipulation
        createCircularRipple(x, y)
    }

    private fun createCircularRipple(x: Float, y: Float) {
        // Create a circular ripple effect at the touch location
        val rippleView = View(this).apply {
            layoutParams = android.widget.FrameLayout.LayoutParams(200, 200)
            // Create a circular background programmatically
            background = createCircularDrawable()
            alpha = 0.6f
            scaleX = 0.1f
            scaleY = 0.1f
            translationX = x - 100f
            translationY = y - 100f
            elevation = 10f
        }

        binding.root.addView(rippleView)

        // Animate the ripple expanding and fading
        rippleView.animate()
            .scaleX(4f)
            .scaleY(4f)
            .alpha(0f)
            .setDuration(500)
            .withEndAction {
                binding.root.removeView(rippleView)
            }
            .start()
    }

    private fun createCircularDrawable(): android.graphics.drawable.Drawable {
        val drawable = android.graphics.drawable.GradientDrawable()
        drawable.shape = android.graphics.drawable.GradientDrawable.OVAL
        drawable.setColor(0x4DFFFFFF) // Semi-transparent white
        drawable.setStroke(4, 0x80FFFFFF.toInt()) // Light border
        return drawable
    }

    private fun performPressAnimation(onComplete: () -> Unit) {
        // Create a satisfying press animation before navigation - animate content only
        val scaleDown = ObjectAnimator.ofFloat(binding.mainContent, "scaleX", 1f, 0.95f)
        val scaleDownY = ObjectAnimator.ofFloat(binding.mainContent, "scaleY", 1f, 0.95f)
        val scaleUp = ObjectAnimator.ofFloat(binding.mainContent, "scaleX", 0.95f, 1.02f)
        val scaleUpY = ObjectAnimator.ofFloat(binding.mainContent, "scaleY", 0.95f, 1.02f)
        val scaleNormal = ObjectAnimator.ofFloat(binding.mainContent, "scaleX", 1.02f, 1f)
        val scaleNormalY = ObjectAnimator.ofFloat(binding.mainContent, "scaleY", 1.02f, 1f)

        val animatorSet = AnimatorSet().apply {
            play(scaleDown).with(scaleDownY)
            play(scaleUp).with(scaleUpY).after(scaleDown)
            play(scaleNormal).with(scaleNormalY).after(scaleUp)
            duration = 200
            interpolator = AccelerateDecelerateInterpolator()
        }

        animatorSet.addListener(object : android.animation.AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: android.animation.Animator) {
                onComplete()
            }
        })

        animatorSet.start()
    }

    private fun setupModalFunctionality() {
        // Question mark button click
        binding.questionMarkButton.setOnClickListener {
            showModal()
        }

        // Close modal button click
        binding.closeModalButton.setOnClickListener {
            hideModal()
        }

        // Click outside modal to close
        binding.modalOverlay.setOnClickListener { view ->
            if (view == binding.modalOverlay) {
                hideModal()
            }
        }

        // Prevent modal content clicks from closing modal
        binding.modalContent.setOnClickListener {
            // Do nothing - prevent click from bubbling up
        }
    }

    private fun showModal() {
        binding.modalOverlay.visibility = View.VISIBLE

        // Animate modal appearance
        binding.modalContent.alpha = 0f
        binding.modalContent.scaleX = 0.8f
        binding.modalContent.scaleY = 0.8f

        binding.modalContent.animate()
            .alpha(1f)
            .scaleX(1f)
            .scaleY(1f)
            .setDuration(300)
            .setInterpolator(AccelerateDecelerateInterpolator())
            .start()
    }

    private fun hideModal() {
        binding.modalContent.animate()
            .alpha(0f)
            .scaleX(0.8f)
            .scaleY(0.8f)
            .setDuration(250)
            .setInterpolator(AccelerateDecelerateInterpolator())
            .withEndAction {
                binding.modalOverlay.visibility = View.GONE
            }
            .start()
    }

    // Prevent back button from exiting app
    override fun onBackPressed() {
        // Do nothing - disable back button
    }

    // Prevent hardware keys from exiting
    override fun onKeyDown(keyCode: Int, event: KeyEvent?): Boolean {
        return when (keyCode) {
            KeyEvent.KEYCODE_HOME,
            KeyEvent.KEYCODE_RECENT_APPS,
            KeyEvent.KEYCODE_BACK -> true // Block these keys
            else -> super.onKeyDown(keyCode, event)
        }
    }

    override fun onWindowFocusChanged(hasFocus: Boolean) {
        super.onWindowFocusChanged(hasFocus)
        if (hasFocus) {
            setupFullScreen()
        }
    }
}

package com.waterfountainmachine.app

import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.view.KeyEvent
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.core.view.WindowInsetsControllerCompat
import com.waterfountainmachine.app.databinding.ActivityPinBinding

class PINActivity : AppCompatActivity() {
    private lateinit var binding: ActivityPinBinding
    private val inactivityHandler = Handler(Looper.getMainLooper())
    private val inactivityTimeout = 30000L // 30 seconds

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityPinBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupFullScreen()
        setupClickListeners()
        startInactivityTimer()
    }

    private fun setupFullScreen() {
        WindowCompat.setDecorFitsSystemWindows(window, false)
        val controller = WindowInsetsControllerCompat(window, binding.root)
        controller.hide(WindowInsetsCompat.Type.systemBars())
        controller.systemBarsBehavior = WindowInsetsControllerCompat.BEHAVIOR_SHOW_TRANSIENT_BARS_BY_SWIPE

        // Hide system UI completely
        binding.root.systemUiVisibility = (View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                or View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                or View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_FULLSCREEN
                or View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY)
    }

    private fun setupClickListeners() {
        binding.backButton.setOnClickListener {
            returnToVendingScreen()
        }
    }

    private fun returnToVendingScreen() {
        finish()
        overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out)
    }

    private fun startInactivityTimer() {
        inactivityHandler.postDelayed({
            returnToMainScreen()
        }, inactivityTimeout)
    }

    private fun returnToMainScreen() {
        // Navigate back to MainActivity
        finish()
        overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out)
    }

    private fun resetInactivityTimer() {
        inactivityHandler.removeCallbacksAndMessages(null)
        startInactivityTimer()
    }

    override fun onUserInteraction() {
        super.onUserInteraction()
        resetInactivityTimer()
    }

    // Prevent back button from exiting app
    override fun onBackPressed() {
        returnToVendingScreen()
    }

    // Prevent hardware keys from exiting
    override fun onKeyDown(keyCode: Int, event: KeyEvent?): Boolean {
        return when (keyCode) {
            KeyEvent.KEYCODE_HOME,
            KeyEvent.KEYCODE_RECENT_APPS -> true // Block these keys
            KeyEvent.KEYCODE_BACK -> {
                returnToVendingScreen()
                true
            }
            else -> super.onKeyDown(keyCode, event)
        }
    }

    override fun onWindowFocusChanged(hasFocus: Boolean) {
        super.onWindowFocusChanged(hasFocus)
        if (hasFocus) {
            setupFullScreen()
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        inactivityHandler.removeCallbacksAndMessages(null)
    }
}

package com.waterfountainmachine.app

import android.content.Intent
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.view.KeyEvent
import android.view.View
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.core.view.WindowInsetsControllerCompat
import com.waterfountainmachine.app.databinding.ActivityVendingBinding

class VendingActivity : AppCompatActivity() {
    private lateinit var binding: ActivityVendingBinding
    private val inactivityHandler = Handler(Looper.getMainLooper())
    private val inactivityTimeout = 30000L // 30 seconds

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityVendingBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupFullScreen()
        setupClickListeners()
        startInactivityTimer()
    }

    private fun setupFullScreen() {
        WindowCompat.setDecorFitsSystemWindows(window, false)
        val controller = WindowInsetsControllerCompat(window, binding.root)
        controller.hide(WindowInsetsCompat.Type.systemBars())
        controller.systemBarsBehavior = WindowInsetsControllerCompat.BEHAVIOR_SHOW_TRANSIENT_BARS_BY_SWIPE

        // Hide system UI completely
        binding.root.systemUiVisibility = (View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                or View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                or View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_FULLSCREEN
                or View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY)
    }

    private fun setupClickListeners() {
        binding.smsCard.setOnClickListener {
            resetInactivityTimer()
            val intent = Intent(this, SMSActivity::class.java)
            startActivity(intent)
            overridePendingTransition(R.anim.slide_in_right, R.anim.slide_out_left)
        }

        binding.qrCodeCard.setOnClickListener {
            resetInactivityTimer()
            val intent = Intent(this, QRActivity::class.java)
            startActivity(intent)
            overridePendingTransition(R.anim.slide_in_right, R.anim.slide_out_left)
        }

        binding.backButton.setOnClickListener {
            returnToMainScreen()
        }
    }

    private fun returnToMainScreen() {
        finish()
        overridePendingTransition(R.anim.slide_in_left, R.anim.slide_out_right)
    }

    private fun startInactivityTimer() {
        inactivityHandler.postDelayed({
            returnToMainScreen()
        }, inactivityTimeout)
    }

    private fun resetInactivityTimer() {
        inactivityHandler.removeCallbacksAndMessages(null)
        startInactivityTimer()
    }

    override fun onUserInteraction() {
        super.onUserInteraction()
        resetInactivityTimer()
    }

    // Prevent back button from exiting app - return to main screen instead
    override fun onBackPressed() {
        returnToMainScreen()
    }

    // Prevent hardware keys from exiting
    override fun onKeyDown(keyCode: Int, event: KeyEvent?): Boolean {
        return when (keyCode) {
            KeyEvent.KEYCODE_HOME,
            KeyEvent.KEYCODE_RECENT_APPS -> true // Block these keys
            KeyEvent.KEYCODE_BACK -> {
                returnToMainScreen()
                true
            }
            else -> super.onKeyDown(keyCode, event)
        }
    }

    override fun onWindowFocusChanged(hasFocus: Boolean) {
        super.onWindowFocusChanged(hasFocus)
        if (hasFocus) {
            setupFullScreen()
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        inactivityHandler.removeCallbacksAndMessages(null)
    }
}

package com.waterfountainmachine.app

import android.animation.AnimatorSet
import android.animation.ObjectAnimator
import android.content.Intent
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.view.KeyEvent
import android.view.View
import android.view.animation.AccelerateDecelerateInterpolator
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.WindowCompat
import androidx.core.view.WindowInsetsCompat
import androidx.core.view.WindowInsetsControllerCompat
import com.waterfountainmachine.app.databinding.ActivityVendingBinding

class VendingActivity : AppCompatActivity() {
    private lateinit var binding: ActivityVendingBinding
    private val inactivityHandler = Handler(Looper.getMainLooper())
    private val inactivityTimeout = 30000L // 30 seconds
    private var questionMarkAnimator: AnimatorSet? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityVendingBinding.inflate(layoutInflater)
        setContentView(binding.root)

        setupFullScreen()
        setupClickListeners()
        setupQuestionMarkAnimation()
        setupModalFunctionality()
        startInactivityTimer()
    }

    private fun setupFullScreen() {
        WindowCompat.setDecorFitsSystemWindows(window, false)
        val controller = WindowInsetsControllerCompat(window, binding.root)
        controller.hide(WindowInsetsCompat.Type.systemBars())
        controller.systemBarsBehavior = WindowInsetsControllerCompat.BEHAVIOR_SHOW_TRANSIENT_BARS_BY_SWIPE

        // Hide system UI completely
        binding.root.systemUiVisibility = (View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                or View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                or View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                or View.SYSTEM_UI_FLAG_FULLSCREEN
                or View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY)
    }

    private fun setupClickListeners() {
        binding.smsCard.setOnClickListener {
            resetInactivityTimer()
            performCardClickAnimation(binding.smsCard) {
                val intent = Intent(this, SMSActivity::class.java)
                startActivity(intent)
                // Use zoom transition for a modern, engaging feel
                overridePendingTransition(R.anim.zoom_in_fade, R.anim.zoom_out_fade)
            }
        }

        // PIN Code card is now disabled - no click listener
        // QR Code card is now properly disabled - no click listener

        binding.backButton.setOnClickListener {
            returnToMainScreen()
        }
    }

    private fun returnToMainScreen() {
        finish()
        overridePendingTransition(android.R.anim.fade_in, android.R.anim.fade_out)
    }

    private fun startInactivityTimer() {
        inactivityHandler.postDelayed({
            returnToMainScreen()
        }, inactivityTimeout)
    }

    private fun resetInactivityTimer() {
        inactivityHandler.removeCallbacksAndMessages(null)
        startInactivityTimer()
    }

    override fun onUserInteraction() {
        super.onUserInteraction()
        resetInactivityTimer()
    }

    // Prevent back button from exiting app - return to main screen instead
    override fun onBackPressed() {
        returnToMainScreen()
    }

    // Prevent hardware keys from exiting
    override fun onKeyDown(keyCode: Int, event: KeyEvent?): Boolean {
        return when (keyCode) {
            KeyEvent.KEYCODE_HOME,
            KeyEvent.KEYCODE_RECENT_APPS -> true // Block these keys
            KeyEvent.KEYCODE_BACK -> {
                returnToMainScreen()
                true
            }
            else -> super.onKeyDown(keyCode, event)
        }
    }

    override fun onWindowFocusChanged(hasFocus: Boolean) {
        super.onWindowFocusChanged(hasFocus)
        if (hasFocus) {
            setupFullScreen()
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        inactivityHandler.removeCallbacksAndMessages(null)
    }

    private fun setupQuestionMarkAnimation() {
        // Create a more frequent and engaging shake animation
        val shakeX = ObjectAnimator.ofFloat(binding.questionMarkButton, "translationX", 0f, -12f, 12f, -8f, 8f, -4f, 4f, 0f)
        shakeX.duration = 1000 // Shorter duration

        val rotate = ObjectAnimator.ofFloat(binding.questionMarkIcon, "rotation", 0f, 20f, -20f, 15f, -15f, 8f, -8f, 0f)
        rotate.duration = 1000

        val scale = ObjectAnimator.ofFloat(binding.questionMarkButton, "scaleX", 1f, 1.15f, 1f)
        scale.duration = 1000

        val scaleY = ObjectAnimator.ofFloat(binding.questionMarkButton, "scaleY", 1f, 1.15f, 1f)
        scaleY.duration = 1000

        questionMarkAnimator = AnimatorSet().apply {
            playTogether(shakeX, rotate, scale, scaleY)
            interpolator = AccelerateDecelerateInterpolator()
            startDelay = 1000 // Start sooner
        }

        // Create repeating animation with much shorter intervals
        questionMarkAnimator?.addListener(object : android.animation.AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: android.animation.Animator) {
                // Much more frequent shaking
                binding.root.postDelayed({
                    if (questionMarkAnimator != null) {
                        questionMarkAnimator?.start()
                    }
                }, 1500) // Only 1.5 second pause between animations
            }
        })

        questionMarkAnimator?.start()
    }

    private fun setupModalFunctionality() {
        // Question mark button click
        binding.questionMarkButton.setOnClickListener {
            resetInactivityTimer()
            showModal()
        }

        // Close modal button click
        binding.closeModalButton.setOnClickListener {
            hideModal()
        }

        // Click outside modal to close
        binding.modalOverlay.setOnClickListener { view ->
            if (view == binding.modalOverlay) {
                hideModal()
            }
        }

        // Prevent modal content clicks from closing modal
        binding.modalContent.setOnClickListener {
            // Do nothing - prevent click from bubbling up
        }
    }

    private fun showModal() {
        binding.modalOverlay.visibility = View.VISIBLE

        // Animate modal appearance
        binding.modalContent.alpha = 0f
        binding.modalContent.scaleX = 0.8f
        binding.modalContent.scaleY = 0.8f

        binding.modalContent.animate()
            .alpha(1f)
            .scaleX(1f)
            .scaleY(1f)
            .setDuration(300)
            .setInterpolator(AccelerateDecelerateInterpolator())
            .start()
    }

    private fun hideModal() {
        binding.modalContent.animate()
            .alpha(0f)
            .scaleX(0.8f)
            .scaleY(0.8f)
            .setDuration(250)
            .setInterpolator(AccelerateDecelerateInterpolator())
            .withEndAction {
                binding.modalOverlay.visibility = View.GONE
            }
            .start()
    }

    private fun performCardClickAnimation(card: View, onComplete: () -> Unit) {
        // Create satisfying Material Design-style press animation
        val scaleDownX = ObjectAnimator.ofFloat(card, "scaleX", 1f, 0.95f)
        val scaleDownY = ObjectAnimator.ofFloat(card, "scaleY", 1f, 0.95f)
        val scaleUpX = ObjectAnimator.ofFloat(card, "scaleX", 0.95f, 1.02f)
        val scaleUpY = ObjectAnimator.ofFloat(card, "scaleY", 0.95f, 1.02f)
        val scaleNormalX = ObjectAnimator.ofFloat(card, "scaleX", 1.02f, 1f)
        val scaleNormalY = ObjectAnimator.ofFloat(card, "scaleY", 1.02f, 1f)

        // Add elevation animation for depth feedback
        val elevateUp = ObjectAnimator.ofFloat(card, "elevation", 8f, 16f)
        val elevateDown = ObjectAnimator.ofFloat(card, "elevation", 16f, 8f)

        val pressSet = AnimatorSet().apply {
            play(scaleDownX).with(scaleDownY).with(elevateUp)
            duration = 100
            interpolator = AccelerateDecelerateInterpolator()
        }

        val bounceSet = AnimatorSet().apply {
            play(scaleUpX).with(scaleUpY)
            duration = 150
            interpolator = AccelerateDecelerateInterpolator()
        }

        val normalizeSet = AnimatorSet().apply {
            play(scaleNormalX).with(scaleNormalY).with(elevateDown)
            duration = 100
            interpolator = AccelerateDecelerateInterpolator()
        }

        val fullAnimation = AnimatorSet().apply {
            playSequentially(pressSet, bounceSet, normalizeSet)
        }

        fullAnimation.addListener(object : android.animation.AnimatorListenerAdapter() {
            override fun onAnimationEnd(animation: android.animation.Animator) {
                onComplete()
            }
        })

        fullAnimation.start()
    }
}

package com.waterfountainmachine.app.hardware.sdk

import java.nio.ByteBuffer
import java.nio.ByteOrder

/**
 * Represents a VMC communication protocol frame
 * Frame format: [ADDR][FRAME_NUMBER][HEADER][CMD][DATA_LENGTH][DATA][CHK]
 */
data class ProtocolFrame(
    val address: Byte = FIXED_ADDRESS,
    val frameNumber: Byte = FIXED_FRAME_NUMBER,
    val header: Byte,
    val command: Byte,
    val dataLength: Byte,
    val data: ByteArray,
    val checksum: Byte
) {
    companion object {
        const val FIXED_ADDRESS: Byte = 0xFF.toByte()
        const val FIXED_FRAME_NUMBER: Byte = 0x00
        const val APP_HEADER: Byte = 0x55
        const val VMC_HEADER: Byte = 0xAA.toByte()

        // Minimum frame size: ADDR + FRAME_NUMBER + HEADER + CMD + DATA_LENGTH + CHK
        const val MIN_FRAME_SIZE = 6
        const val MAX_DATA_SIZE = 255
    }

    /**
     * Convert frame to byte array for transmission
     */
    fun toByteArray(): ByteArray {
        val frameSize = MIN_FRAME_SIZE + data.size
        val buffer = ByteBuffer.allocate(frameSize).order(ByteOrder.LITTLE_ENDIAN)

        buffer.put(address)
        buffer.put(frameNumber)
        buffer.put(header)
        buffer.put(command)
        buffer.put(dataLength)
        buffer.put(data)
        buffer.put(checksum)

        return buffer.array()
    }

    /**
     * Calculate checksum for this frame
     * Checksum = sum of all bytes except ADDR, FRAME_NUMBER, and CHK (lower 8 bits)
     */
    fun calculateChecksum(): Byte {
        var sum = (header.toInt() and 0xFF) +
                  (command.toInt() and 0xFF) +
                  (dataLength.toInt() and 0xFF)

        for (b in data) {
            sum += (b.toInt() and 0xFF)
        }

        return (sum and 0xFF).toByte()
    }

    /**
     * Validate frame checksum
     */
    fun isValid(): Boolean {
        return checksum == calculateChecksum()
    }

    override fun equals(other: Any?): Boolean {
        if (this === other) return true
        if (javaClass != other?.javaClass) return false

        other as ProtocolFrame

        if (address != other.address) return false
        if (frameNumber != other.frameNumber) return false
        if (header != other.header) return false
        if (command != other.command) return false
        if (dataLength != other.dataLength) return false
        if (!data.contentEquals(other.data)) return false
        if (checksum != other.checksum) return false

        return true
    }

    override fun hashCode(): Int {
        var result = address.toInt()
        result = 31 * result + frameNumber.toInt()
        result = 31 * result + header.toInt()
        result = 31 * result + command.toInt()
        result = 31 * result + dataLength.toInt()
        result = 31 * result + data.contentHashCode()
        result = 31 * result + checksum.toInt()
        return result
    }
}

/**
 * Builder for creating protocol frames
 */
class ProtocolFrameBuilder {
    private var header: Byte = ProtocolFrame.APP_HEADER
    private var command: Byte = 0
    private var data: ByteArray = byteArrayOf()

    fun appHeader() = apply { header = ProtocolFrame.APP_HEADER }
    fun vmcHeader() = apply { header = ProtocolFrame.VMC_HEADER }
    fun command(cmd: Byte) = apply { command = cmd }
    fun data(data: ByteArray) = apply { this.data = data }
    fun dataBytes(vararg bytes: Byte) = apply { this.data = bytes }

    fun build(): ProtocolFrame {
        val dataLength = (data.size and 0xFF).toByte()
        val frame = ProtocolFrame(
            header = header,
            command = command,
            dataLength = dataLength,
            data = data,
            checksum = 0 // Temporary
        )

        return frame.copy(checksum = frame.calculateChecksum())
    }
}

/**
 * Parser for incoming protocol frames
 */
object ProtocolFrameParser {

    /**
     * Parse byte array into ProtocolFrame
     * @param bytes Raw bytes received from VMC
     * @return Parsed frame or null if invalid
     */
    fun parse(bytes: ByteArray): ProtocolFrame? {
        if (bytes.size < ProtocolFrame.MIN_FRAME_SIZE) {
            return null
        }

        val buffer = ByteBuffer.wrap(bytes).order(ByteOrder.LITTLE_ENDIAN)

        val address = buffer.get()
        val frameNumber = buffer.get()
        val header = buffer.get()
        val command = buffer.get()
        val dataLength = buffer.get()

        val dataSize = dataLength.toInt() and 0xFF
        if (buffer.remaining() < dataSize + 1) { // +1 for checksum
            return null
        }

        val data = ByteArray(dataSize)
        buffer.get(data)
        val checksum = buffer.get()

        val frame = ProtocolFrame(
            address = address,
            frameNumber = frameNumber,
            header = header,
            command = command,
            dataLength = dataLength,
            data = data,
            checksum = checksum
        )

        return if (frame.isValid()) frame else null
    }
}

package com.waterfountainmachine.app.hardware.sdk

import java.nio.ByteBuffer
import java.nio.ByteOrder

/**
 * Represents a VMC communication protocol frame
 * Frame format: [ADDR][FRAME_NUMBER][HEADER][CMD][DATA_LENGTH][DATA][CHK]
 */
data class ProtocolFrame(
    val address: Byte = FIXED_ADDRESS,
    val frameNumber: Byte = FIXED_FRAME_NUMBER,
    val header: Byte,
    val command: Byte,
    val dataLength: Byte,
    val data: ByteArray,
    val checksum: Byte
) {
    companion object {
        const val FIXED_ADDRESS: Byte = 0xFF.toByte()
        const val FIXED_FRAME_NUMBER: Byte = 0x00
        const val APP_HEADER: Byte = 0x55
        const val VMC_HEADER: Byte = 0xAA.toByte()

        // Minimum frame size: ADDR + FRAME_NUMBER + HEADER + CMD + DATA_LENGTH + CHK
        const val MIN_FRAME_SIZE = 6
        const val MAX_DATA_SIZE = 255
    }

    /**
     * Convert frame to byte array for transmission
     */
    fun toByteArray(): ByteArray {
        val frameSize = MIN_FRAME_SIZE + data.size
        val buffer = ByteBuffer.allocate(frameSize).order(ByteOrder.LITTLE_ENDIAN)

        buffer.put(address)
        buffer.put(frameNumber)
        buffer.put(header)
        buffer.put(command)
        buffer.put(dataLength)
        buffer.put(data)
        buffer.put(checksum)

        return buffer.array()
    }

    /**
     * Calculate checksum for this frame
     * Checksum = sum of all bytes except ADDR, FRAME_NUMBER, and CHK (lower 8 bits)
     */
    fun calculateChecksum(): Byte {
        var sum = (header.toInt() and 0xFF) + 
                  (command.toInt() and 0xFF) + 
                  (dataLength.toInt() and 0xFF)

        for (b in data) {
            sum += (b.toInt() and 0xFF)
        }

        return (sum and 0xFF).toByte()
    }

    /**
     * Validate frame checksum
     */
    fun isValid(): Boolean {
        return checksum == calculateChecksum()
    }

    override fun equals(other: Any?): Boolean {
        if (this === other) return true
        if (javaClass != other?.javaClass) return false

        other as ProtocolFrame

        if (address != other.address) return false
        if (frameNumber != other.frameNumber) return false
        if (header != other.header) return false
        if (command != other.command) return false
        if (dataLength != other.dataLength) return false
        if (!data.contentEquals(other.data)) return false
        if (checksum != other.checksum) return false

        return true
    }

    override fun hashCode(): Int {
        var result = address.toInt()
        result = 31 * result + frameNumber.toInt()
        result = 31 * result + header.toInt()
        result = 31 * result + command.toInt()
        result = 31 * result + dataLength.toInt()
        result = 31 * result + data.contentHashCode()
        result = 31 * result + checksum.toInt()
        return result
    }
}

package com.waterfountainmachine.app.hardware.sdk

/**
 * Builder for creating protocol frames
 */
class ProtocolFrameBuilder {
    private var header: Byte = ProtocolFrame.APP_HEADER
    private var command: Byte = 0x00
    private var data: ByteArray = byteArrayOf()

    fun appHeader(): ProtocolFrameBuilder {
        header = ProtocolFrame.APP_HEADER
        return this
    }

    fun vmcHeader(): ProtocolFrameBuilder {
        header = ProtocolFrame.VMC_HEADER
        return this
    }

    fun command(cmd: Byte): ProtocolFrameBuilder {
        command = cmd
        return this
    }

    fun data(data: ByteArray): ProtocolFrameBuilder {
        this.data = data.copyOf()
        return this
    }

    fun dataBytes(vararg bytes: Byte): ProtocolFrameBuilder {
        this.data = bytes
        return this
    }

    fun build(): ProtocolFrame {
        val dataLength = data.size.toByte()

        // Calculate checksum: header + command + dataLength + data
        var checksum = (header.toInt() and 0xFF) + 
                      (command.toInt() and 0xFF) + 
                      (dataLength.toInt() and 0xFF)

        for (b in data) {
            checksum += (b.toInt() and 0xFF)
        }

        val finalChecksum = (checksum and 0xFF).toByte()

        return ProtocolFrame(
            address = ProtocolFrame.FIXED_ADDRESS,
            frameNumber = ProtocolFrame.FIXED_FRAME_NUMBER,
            header = header,
            command = command,
            dataLength = dataLength,
            data = data.copyOf(),
            checksum = finalChecksum
        )
    }
}

package com.waterfountainmachine.app.hardware.sdk

import java.nio.ByteBuffer
import java.nio.ByteOrder

/**
 * Parser for protocol frames from byte arrays
 */
object ProtocolFrameParser {

    /**
     * Parse byte array into ProtocolFrame
     * @param bytes Raw bytes received from VMC
     * @return Parsed frame or null if invalid
     */
    fun parse(bytes: ByteArray): ProtocolFrame? {
        if (bytes.size < ProtocolFrame.MIN_FRAME_SIZE) {
            return null
        }

        try {
            val buffer = ByteBuffer.wrap(bytes).order(ByteOrder.LITTLE_ENDIAN)

            val address = buffer.get()
            val frameNumber = buffer.get()
            val header = buffer.get()
            val command = buffer.get()
            val dataLength = buffer.get()

            val dataLengthInt = dataLength.toInt() and 0xFF
            if (buffer.remaining() < dataLengthInt + 1) { // +1 for checksum
                return null
            }

            val data = ByteArray(dataLengthInt)
            if (dataLengthInt > 0) {
                buffer.get(data)
            }

            val checksum = buffer.get()

            val frame = ProtocolFrame(
                address = address,
                frameNumber = frameNumber,
                header = header,
                command = command,
                dataLength = dataLength,
                data = data,
                checksum = checksum
            )

            return if (frame.isValid()) frame else null

        } catch (e: Exception) {
            return null
        }
    }
}

package com.waterfountainmachine.app.hardware.sdk

import kotlinx.coroutines.runBlocking

/**
 * Simple test runner to verify our SDK implementation works
 * This bypasses the Gradle test configuration issue
 */
fun main() {
    println(" Running VMC SDK Tests...")

    // Test 1: Protocol Frame Creation and Validation
    println("\n1️⃣ Testing Protocol Frame Creation...")
    testProtocolFrameCreation()

    // Test 2: VMC Command Builder
    println("\n2️⃣ Testing VMC Command Builder...")
    testVmcCommandBuilder()

    // Test 3: SDK Basic Operations
    println("\n3️⃣ Testing SDK Basic Operations...")
    testSDKOperations()

    println("\n✅ All tests completed!")
}

fun testProtocolFrameCreation() {
    try {
        // Create a test frame
        val frame = ProtocolFrameBuilder()
            .appHeader()
            .command(0x31)
            .dataBytes(0xAD.toByte())
            .build()

        // Verify frame structure
        assert(frame.address == ProtocolFrame.FIXED_ADDRESS) { "Wrong address" }
        assert(frame.header == ProtocolFrame.APP_HEADER) { "Wrong header" }
        assert(frame.command == 0x31.toByte()) { "Wrong command" }
        assert(frame.dataLength == 1.toByte()) { "Wrong data length" }
        assert(frame.data.contentEquals(byteArrayOf(0xAD.toByte()))) { "Wrong data" }
        assert(frame.isValid()) { "Frame validation failed" }

        println("   ✅ Protocol frame creation and validation works correctly")
    } catch (e: Exception) {
        println("   ❌ Protocol frame test failed: ${e.message}")
    }
}

fun testVmcCommandBuilder() {
    try {
        // Test getDeviceId command
        val deviceIdFrame = VmcCommandBuilder.getDeviceId()
        assert(deviceIdFrame.command == VmcCommands.GET_DEVICE_ID) { "Wrong device ID command" }
        assert(deviceIdFrame.isValid()) { "Device ID frame invalid" }

        // Test delivery command
        val deliveryFrame = VmcCommandBuilder.deliveryCommand(5, 2)
        assert(deliveryFrame.command == VmcCommands.DELIVERY_COMMAND) { "Wrong delivery command" }
        assert(deliveryFrame.data.contentEquals(byteArrayOf(5, 2))) { "Wrong delivery data" }
        assert(deliveryFrame.isValid()) { "Delivery frame invalid" }

        // Test remove fault command
        val faultFrame = VmcCommandBuilder.removeFault()
        assert(faultFrame.command == VmcCommands.REMOVE_FAULT) { "Wrong fault command" }
        assert(faultFrame.isValid()) { "Fault frame invalid" }

        println("   ✅ VMC command builder works correctly")
    } catch (e: Exception) {
        println("   ❌ VMC command builder test failed: ${e.message}")
    }
}

fun testSDKOperations() = runBlocking {
    try {
        // Create mock serial communicator
        val mockSerial = MockSerialCommunicator()
        val sdk = VendingMachineSDKImpl(mockSerial)

        // Test connection
        val connected = sdk.connect()
        assert(connected) { "Connection failed" }
        assert(sdk.isConnected()) { "Not showing as connected" }

        // Test getDeviceId with mock response
        val deviceId = "WF001234567890\u0000"
        val responseFrame = ProtocolFrameBuilder()
            .vmcHeader()
            .command(VmcCommands.GET_DEVICE_ID)
            .data(deviceId.toByteArray(Charsets.UTF_8).take(15).toByteArray())
            .build()
        mockSerial.queueResponse(responseFrame.toByteArray())

        val result = sdk.getDeviceId()
        assert(result.isSuccess) { "getDeviceId failed: ${result.exceptionOrNull()?.message}" }

        // Test delivery command
        val deliveryResponseFrame = ProtocolFrameBuilder()
            .vmcHeader()
            .command(VmcCommands.DELIVERY_COMMAND)
            .dataBytes(5, 2)
            .build()
        mockSerial.queueResponse(deliveryResponseFrame.toByteArray())

        val deliveryResult = sdk.sendDeliveryCommand(5, 2)
        assert(deliveryResult.isSuccess) { "sendDeliveryCommand failed: ${deliveryResult.exceptionOrNull()?.message}" }

        // Test disconnect
        sdk.disconnect()
        assert(!sdk.isConnected()) { "Still showing as connected after disconnect" }

        println("   ✅ SDK operations work correctly")
    } catch (e: Exception) {
        println("   ❌ SDK operations test failed: ${e.message}")
    }
}

package com.waterfountainmachine.app.hardware.sdk
/** * Main SDK interface for VMC communication * This is the primary interface that applications should use */ interface VendingMachineSDK {
/** * Initialize connection to VMC * @param config Serial configuration * @return true if connection successful */ suspend fun connect(config: SerialConfig = SerialConfig()): Boolean
/** * Disconnect from VMC */ suspend fun disconnect()
/** * Check if connected to VMC */ fun isConnected(): Boolean
/** * Get VMC device ID * @return Device ID string (15 characters) or null if failed */ suspend fun getDeviceId(): Result<String>
/** * Send delivery command to dispense water * @param slot Slot number (1-255) * @param quantity Quantity to dispense (typically 1) * @return Delivery response with slot and quantity confirmation */ suspend fun sendDeliveryCommand(slot: Int, quantity: Int = 1): Result<VmcResponse.DeliveryResponse>
/** * Query delivery status * @param slot Slot number that was used for delivery * @param quantity Expected quantity * @return Status response indicating success/failure */ suspend fun queryDeliveryStatus(slot: Int, quantity: Int = 1): Result<VmcResponse.StatusResponse>
/** * Remove any faults in the VMC * @return Status response indicating success/failure */ suspend fun removeFault(): Result<VmcResponse.StatusResponse> }

package com.waterfountainmachine.app.hardware.sdk
/** * Main SDK interface for VMC communication * This is the primary interface that applications should use */ interface VendingMachineSDK {
/** * Initialize connection to VMC * @param config Serial configuration * @return true if connection successful */ suspend fun connect(config: SerialConfig = SerialConfig()): Boolean
/** * Disconnect from VMC */ suspend fun disconnect()
/** * Check if connected to VMC */ fun isConnected(): Boolean
/** * Get VMC device ID * @return Device ID string (15 characters) or null if failed */ suspend fun getDeviceId(): Result<String>
/** * Send delivery command to dispense water * @param slot Slot number (1-255) * @param quantity Quantity to dispense (typically 1) * @return Delivery response with slot and quantity confirmation */ suspend fun sendDeliveryCommand(slot: Int, quantity: Int = 1): Result<VmcResponse.DeliveryResponse>
/** * Query delivery status * @param slot Slot number that was used for delivery * @param quantity Expected quantity * @return Status response indicating success/failure */ suspend fun queryDeliveryStatus(slot: Int, quantity: Int = 1): Result<VmcResponse.StatusResponse>
/** * Remove any faults in the VMC * @return Status response indicating success/failure */ suspend fun removeFault(): Result<VmcResponse.StatusResponse>
/** * Query VMC balance (for coin-operated machines) * @return Balance in cents */ suspend fun queryBalance(): Result<Int>
/** * Clear any faults in the VMC * @return true if fault clearing successful */ suspend fun clearFaults(): Result<Boolean>
/** * Dispense water from specified slot * @param slot Slot number * @return Dispense result */ suspend fun dispenseWater(slot: Int): Result<VmcResponse.DeliveryResponse> }

package com.waterfountainmachine.app.hardware.sdk

import kotlinx.coroutines.TimeoutCancellationException
import kotlinx.coroutines.withTimeout
import java.nio.ByteBuffer
import java.nio.ByteOrder

/**
 * Implementation of VendingMachineSDK
 */
class VendingMachineSDKImpl(
    private val serialCommunicator: SerialCommunicator,
    private val commandTimeoutMs: Long = 5000,
    private val statusPollingIntervalMs: Long = 200,
    private val maxStatusPollingAttempts: Int = 10
) : VendingMachineSDK {

    override suspend fun connect(config: SerialConfig): Boolean {
        return serialCommunicator.connect(config)
    }

    override suspend fun disconnect() {
        serialCommunicator.disconnect()
    }

    override fun isConnected(): Boolean {
        return serialCommunicator.isConnected()
    }

    override suspend fun getDeviceId(): Result<String> {
        return try {
            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.getDeviceId()
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.GET_DEVICE_ID) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            val deviceId = String(responseFrame.data, Charsets.UTF_8).trim('\u0000')
            Result.success(deviceId)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }

    override suspend fun sendDeliveryCommand(slot: Int, quantity: Int): Result<VmcResponse.DeliveryResponse> {
        return try {
            if (slot < 1 || slot > 255) {
                throw IllegalArgumentException("Slot must be between 1 and 255")
            }
            if (quantity < 1 || quantity > 255) {
                throw IllegalArgumentException("Quantity must be between 1 and 255")
            }

            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.deliveryCommand(slot.toByte(), quantity.toByte())
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.DELIVERY_COMMAND) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            if (responseFrame.data.size < 2) {
                return Result.failure(VmcException.ProtocolException("Invalid delivery response data"))
            }

            val deliveryResponse = VmcResponse.DeliveryResponse(
                cargoLane = responseFrame.data[0],
                quantity = responseFrame.data[1]
            )
            Result.success(deliveryResponse)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }

    override suspend fun queryDeliveryStatus(slot: Int, quantity: Int): Result<VmcResponse.StatusResponse> {
        return try {
            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.queryDeliveryStatus(slot.toByte(), quantity.toByte())
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.QUERY_DELIVERY_STATUS) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            if (responseFrame.data.isEmpty()) {
                return Result.failure(VmcException.ProtocolException("Invalid status response data"))
            }

            val status = when (responseFrame.data[0]) {
                VmcStatus.SUCCESS -> VmcResponse.DeliveryStatus.SUCCESS
                VmcStatus.FAILURE -> VmcResponse.DeliveryStatus.FAILURE
                VmcStatus.PENDING -> VmcResponse.DeliveryStatus.PENDING
                else -> VmcResponse.DeliveryStatus.UNKNOWN
            }

            val statusResponse = VmcResponse.StatusResponse(
                status = status,
                cargoLane = if (responseFrame.data.size > 1) responseFrame.data[1] else 0,
                quantity = if (responseFrame.data.size > 2) responseFrame.data[2] else 0
            )
            Result.success(statusResponse)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }

    override suspend fun removeFault(): Result<VmcResponse.StatusResponse> {
        return try {
            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.removeFault()
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.REMOVE_FAULT) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            val status = if (responseFrame.data.isNotEmpty()) {
                when (responseFrame.data[0]) {
                    VmcStatus.SUCCESS -> VmcResponse.DeliveryStatus.SUCCESS
                    VmcStatus.FAILURE -> VmcResponse.DeliveryStatus.FAILURE
                    else -> VmcResponse.DeliveryStatus.UNKNOWN
                }
            } else {
                VmcResponse.DeliveryStatus.SUCCESS
            }

            val statusResponse = VmcResponse.StatusResponse(
                status = status,
                cargoLane = 0,
                quantity = 0
            )
            Result.success(statusResponse)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }

    override suspend fun queryBalance(): Result<Int> {
        return try {
            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.queryBalance()
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.QUERY_BALANCE) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            if (responseFrame.data.size < 4) {
                return Result.failure(VmcException.ProtocolException("Invalid balance response data"))
            }

            // Parse balance from little-endian bytes
            val balance = ByteBuffer.wrap(responseFrame.data).order(ByteOrder.LITTLE_ENDIAN).int
            Result.success(balance)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }

    override suspend fun clearFaults(): Result<Boolean> {
        return try {
            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.clearFaults()
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.CLEAR_FAULTS) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            val success = responseFrame.data.isNotEmpty() && responseFrame.data[0] == VmcStatus.SUCCESS
            Result.success(success)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }

    override suspend fun dispenseWater(slot: Int): Result<VmcResponse.DeliveryResponse> {
        return try {
            if (slot < 1 || slot > 255) {
                throw IllegalArgumentException("Slot must be between 1 and 255")
            }

            if (!isConnected()) {
                return Result.failure(VmcException.ConnectionException("Not connected to VMC"))
            }

            val command = VmcCommandBuilder.dispenseWater(slot.toByte())
            val sendSuccess = serialCommunicator.sendData(command.toByteArray())
            if (!sendSuccess) {
                return Result.failure(VmcException.CommunicationException("Failed to send command"))
            }

            val response = withTimeout(commandTimeoutMs) {
                serialCommunicator.readData(commandTimeoutMs)
            } ?: return Result.failure(VmcException.TimeoutException("No response received"))

            val responseFrame = ProtocolFrameParser.parse(response)
                ?: return Result.failure(VmcException.ProtocolException("Invalid response format"))

            if (responseFrame.command != VmcCommands.DISPENSE_WATER) {
                return Result.failure(VmcException.ProtocolException("Unexpected response command"))
            }

            if (responseFrame.data.size < 2) {
                return Result.failure(VmcException.ProtocolException("Invalid dispense response data"))
            }

            val deliveryResponse = VmcResponse.DeliveryResponse(
                cargoLane = responseFrame.data[0],
                quantity = responseFrame.data[1]
            )
            Result.success(deliveryResponse)
        } catch (e: TimeoutCancellationException) {
            Result.failure(VmcException.TimeoutException("Command timed out"))
        } catch (e: Exception) {
            Result.failure(VmcException.CommunicationException("Communication error: ${e.message}"))
        }
    }
}

package com.waterfountainmachine.app.hardware.sdk

/**
 * Builder for VMC command frames
 */
object VmcCommandBuilder {

    fun getDeviceId(): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.GET_DEVICE_ID)
            .dataBytes(0xAD.toByte())
            .build()
    }

    fun deliveryCommand(cargoLane: Byte, quantity: Byte = 1): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.DELIVERY_COMMAND)
            .dataBytes(cargoLane, quantity)
            .build()
    }

    fun queryDeliveryStatus(cargoLane: Byte, quantity: Byte = 1): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.QUERY_DELIVERY_STATUS)
            .dataBytes(cargoLane, quantity)
            .build()
    }

    fun removeFault(): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.REMOVE_FAULT)
            .dataBytes(0xFF.toByte())
            .build()
    }

    fun queryBalance(): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.QUERY_BALANCE)
            .dataBytes(0x00.toByte())
            .build()
    }

    fun clearFaults(): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.CLEAR_FAULTS)
            .dataBytes(0xFF.toByte())
            .build()
    }

    fun dispenseWater(slot: Byte): ProtocolFrame {
        return ProtocolFrameBuilder()
            .appHeader()
            .command(VmcCommands.DISPENSE_WATER)
            .dataBytes(slot)
            .build()
    }
}

package com.waterfountainmachine.app.hardware.sdk

/**
 * VMC Protocol Commands
 */
object VmcCommands {
    const val GET_DEVICE_ID: Byte = 0x31
    const val DELIVERY_COMMAND: Byte = 0x32
    const val QUERY_DELIVERY_STATUS: Byte = 0x33
    const val REMOVE_FAULT: Byte = 0x34
    const val QUERY_BALANCE: Byte = 0x35
    const val CLEAR_FAULTS: Byte = 0x36
    const val DISPENSE_WATER: Byte = 0x37
}

/**
 * VMC Status Codes
 */
object VmcStatus {
    const val SUCCESS: Byte = 0x00
    const val FAILURE: Byte = 0x01
    const val PENDING: Byte = 0x02
}

/**
 * VMC Response Data Classes
 */
sealed class VmcResponse {

    data class DeliveryResponse(
        val cargoLane: Byte,
        val quantity: Byte
    ) : VmcResponse()

    data class StatusResponse(
        val status: DeliveryStatus,
        val cargoLane: Byte,
        val quantity: Byte
    ) : VmcResponse()

    enum class DeliveryStatus {
        SUCCESS,
        FAILURE,
        PENDING,
        UNKNOWN
    }
}

/**
 * VMC Exception Types
 */
sealed class VmcException(message: String, cause: Throwable? = null) : Exception(message, cause) {
    class ConnectionException(message: String) : VmcException(message)
    class TimeoutException(message: String) : VmcException(message)
    class ProtocolException(message: String) : VmcException(message)
    class CommunicationException(message: String) : VmcException(message)
}

package com.waterfountainmachine.app.hardware.sdk

import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.flowOf
import java.util.concurrent.ConcurrentLinkedQueue

/**
 * Mock implementation of SerialCommunicator for testing
 */
class MockSerialCommunicator : SerialCommunicator {

    private var _isConnected = false
    private val sentCommands = mutableListOf<ByteArray>()
    private val responseQueue = ConcurrentLinkedQueue<ByteArray>()

    override suspend fun connect(config: SerialConfig): Boolean {
        _isConnected = true
        return true
    }

    override suspend fun disconnect() {
        _isConnected = false
        sentCommands.clear()
        responseQueue.clear()
    }

    override fun isConnected(): Boolean {
        return _isConnected
    }

    override suspend fun sendData(data: ByteArray): Boolean {
        if (!_isConnected) {
            return false
        }

        sentCommands.add(data.copyOf())
        return true
    }

    override suspend fun readData(timeoutMs: Long): ByteArray? {
        return responseQueue.poll()
    }

    override fun getDataFlow(): Flow<ByteArray> {
        return flowOf() // Empty flow for testing
    }

    override suspend fun clearBuffers() {
        responseQueue.clear()
    }

    // Test helper methods
    fun queueResponse(response: ByteArray) {
        responseQueue.offer(response.copyOf())
    }

    fun getSentCommands(): List<ByteArray> {
        return sentCommands.map { it.copyOf() }
    }

    fun clearSentCommands() {
        sentCommands.clear()
    }

    // Legacy method for compatibility with existing tests
    suspend fun sendCommand(data: ByteArray): ByteArray? {
        sendData(data)
        return readData()
    }
}

